import img from './photos/Instagram.png';
import Post from './Post';
import img1  from "./photos/main_post.jpg";
import img2  from "./photos/main_post2.jpg";
import img3  from "./photos/main_post3.jpg";
import {useState}  from 'react';

function App() { 
  const [posts, setposts] = useState([
{    
    imgeurl: "img1" ,
    username: "virat121", 
    caption: "ohh what a shot"
} ,
{    
    imgeurl: "Z" ,
    username: "Dhoni324", 
    caption: "what an ton!!"
} ,
{    
    imgeurl: img3 ,
    username: "abd_34", 
    caption: "360 rocket"
} 
 ]);
  return (

   <div className="app bg-stone-100 h-screen">
      <div className="app_header bg-white p-5 border-b-gray-300 border-b-2">
        <img src={img} alt="" className="header_image object-contain"/>
      </div>
    {
      posts.map((value)=>{
        console.log(value);
        return <Post username={value.imgeurl} caption={value.username} imgeurl={value.imgeurl}/>;
      }
      )
    }
    {/* <Post username="jnjwe" caption="nejf" imgeurl={img3}/> */}
   </div>
  );
}

export default App;
