import img from './photos/Instagram.png';
import Post from './Post';
import img1  from "./photos/main_post.jpg";
import img2  from "./photos/main_post2.jpg";
import img3  from "./photos/main_post3.jpg";
import {useState, useEffect}  from 'react';
import { db} from "./firebse";
import { collection, getDocs } from "firebase/firestore";

function App() { 
  const [posts, setposts] = useState([]);
  const [open, setopen] = useState(false);

  useEffect(()=>{
  const postcollection = collection(db, 'posts');
  getDocs(postcollection)
  .then(responce => {
   const  all_post = responce.docs.map(doc=>(
   {
    data: doc.data(),
    id: doc.id,
   })); 
   setposts(all_post);
  })
  .catch(error => console.log(error.message));
  // console.log(posts);
 }, [])
  return (
   <div className="app bg-stone-100 h-screen">

      <Modal
        open={open}
        onClose={()=>setopen(false)} >
        {body}
      </Modal>

      <div className="app_header bg-white p-5 border-b-gray-300 border-b-2">
        <img src={img} alt="" className="header_image object-contain"/>
      </div>
    {
      posts.map((value)=>{
        {/* console.log(value); */}
        return <Post id={value.id} imgeurl={value.data.imgeurl} username={value.data.username} 
        caption={value.data.caption} />;
      })
    }  
   </div>
  );
}

export default App;
